fn find_header(input: &[u8], len: usize) -> Option<usize> {
    let mut counter = [0u8; 256];
    input[..len - 1]
        .iter()
        .for_each(|&c| counter[c as usize] += 1);
    input.windows(len).enumerate().find_map(|(i, w)| {
        counter[w[len - 1] as usize] += 1;
        if counter[b'a' as usize..=b'z' as usize]
            .iter()
            .all(|&v| v <= 1)
        {
            return Some(i + len);
        }
        counter[w[0] as usize] -= 1;
        None
    })
}

fn task1(input: &str) {
    let index = find_header(input.as_bytes(), 4).unwrap();
    println!("index: {}", index);
}

fn task2(input: &str) {
    let index = find_header(input.as_bytes(), 14).unwrap();
    println!("index: {}", index);
}

pub fn tasks() {
    let input = "";
    task1(&input);
    task2(&input);
}
